funcion suma_de_arreglo(numeros, dimension)
    // Variable para almacenar el resultado
    suma_total = 0

    // Iterar por cada elemento del arreglo
    para pos desde 0 hasta dimension-1 hacer
        suma_total = suma_total + numeros[pos]
    fin para

    // Devolver el resultado final
    retornar suma_total
fin funcion

funcion sumar_valores_array(elementos, cantidad)
    // Comenzar con suma en cero
    suma = 0

    // Procesar cada elemento
    para i_actual desde 0 hasta cantidad-1 hacer
        // Agregar elemento actual a la suma
        suma = suma + elementos[i_actual]
    fin para

    // Retornar suma calculada
    retornar suma
fin funcion
